html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}

<style>
    .table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: #B7CCF2;
    color:#000000;
    font-size:16px;
    }
    .print-format table, .print-format tr, 
    .print-format td, .print-format div, .print-format p {
        font-family: sans-serif;
        line-height: 90%;
        vertical-align: middle;
    }
    @media screen {
        .print-format {
            width: 210mm;
            padding: 10mm;
            min-height: 297mm;
            footer {page-break-after: always;}
        }
    }
</style>

<small>
{{ add_header(0,1,doc,letter_head, no_letterhead) }}
{% set is_export = frappe.db.get_value("Sales Taxes and Charges Template", doc.taxes_and_charges, "is_export") %}
<table class="table table-condensed table-bordered">
  <tr>
    {%- if doc.taxes_and_charges == 'OGL'-%}
        <td colspan="3">
            <p style="text-align:center; font-size:14px"><b>{{ _("PROFORMA INVOICE") }}</b></p>
        </td>
    {% elif 'PROFORMA' in doc.name %}
        <td colspan="2">
            <p style="text-align:center; font-size:14px"><b>{{ _("PROFORMA INVOICE") }}</b></p>
        </td>
        <td style="width: 34%;">
            <p style="text-align:right; font-size:14px"><b>{{ _("PI#: ") }} {{ doc.name }}</b></p>
        </td>
    {% else %}
        <td colspan="1">
        {%- if is_export == 1 -%}
            <p style="text-align:left; font-size:14px"><b>{{ _("CUSTOMS INVOICE") }}</b></p>
        {%- endif -%}
        </td>
        <td style="width: 50%;">
            <p style="text-align:right; font-size:14px"><b>{{ _("Invoice #: ") }} {{ doc.name }}</b></p>
        </td>
    {%- endif -%}
  </tr>
</table>
{% set exp_name = frappe.db.get_value("Address", doc.company_address, "address_title") %}
{% set exp_add1 = frappe.db.get_value("Address", doc.company_address, "address_line1") %}
{% set exp_add2 = frappe.db.get_value("Address", doc.company_address, "address_line2") %}
{% set exp_city = frappe.db.get_value("Address", doc.company_address, "city") %}
{% set exp_state = frappe.db.get_value("Address", doc.company_address, "state") %}
{% set exp_country = frappe.db.get_value("Address", doc.company_address, "country") %}
{% set exp_email = frappe.db.get_value("Address", doc.company_address, "email_id") %}
{% set exp_phone = frappe.db.get_value("Address", doc.company_address, "phone") %}
{% set exp_pincode = frappe.db.get_value("Address", doc.company_address, "pincode") %}
{% set exp_gstin = frappe.db.get_value("Address", doc.company_address, "gstin") %}
{% set exp_iec = frappe.db.get_value("Sales Taxes and Charges Template", doc.taxes_and_charges, "iec_code") %}
{% set exp_ad_code = frappe.db.get_value("Sales Taxes and Charges Template", doc.taxes_and_charges, "bank_ad_code") %}
{% set exp_ifsc = frappe.db.get_value("Sales Taxes and Charges Template", doc.taxes_and_charges, "bank_ifsc_code") %}
{% set exp_lut = frappe.db.get_value("Sales Taxes and Charges Template", doc.taxes_and_charges, "lut_no_and_date") %}
{% set exp_port = frappe.db.get_value("Transporters", doc.transporters, "port_code") %}
{% set des_country = frappe.db.get_value("Address", doc.shipping_address_name, "country") %}
{% set des_seaport = frappe.db.get_value("Address", doc.shipping_address_name, "sea_port") %}
{% set des_airport = frappe.db.get_value("Address", doc.shipping_address_name, "airport") %}
{% set pay_terms = frappe.db.get_value("Payment Terms Template", doc.payment_terms_template, "description") %}

<table class="table table-condensed table-bordered">
    <tr>
    <td colspan=2>
        <b>{{ _("Exporter:") }} {{ exp_name }} </b><br>
        <b>{{ _("Address: ") }} </b> {{ exp_add1 }} , {{ exp_add2 }}, {{ exp_city }} {{ exp_state }} {{ exp_pincode }} {{ exp_country }} <br>
        <b>{{ _("Phone: ") }}</b> {{ exp_phone }} <b>{{ _("Email: ") }} </b> {{ exp_email }} <b>{{ _(" GSTIN: ") }}</b> {{ exp_gstin }} <b>{{ _(" IEC: ") }}</b> {{ exp_iec }} <br>
    </td>
    </tr>
    <tr>
    <td style="width: 50%;">
        <b>{{ _("Origin:") }} </b>{{ exp_country }}
    </td>
    <td style="width: 50%;">
        <b>{{ _("Port of Loading:") }} </b>{{ exp_port }}
    </td>
    </tr>
    <tr>
    <td style="width: 50%;">
        <b>{{ _("Destination:") }}</b> {{ des_country }}
    </td>
    <td style="width: 50%;">
        <b>{{ _("Port of Discharge:") }} </b>
        {%- if doc.mode_of_transport == "Ship" -%}
            {{ des_seaport }}
        {%- elif doc.mode_of_transport == "Air" -%}
            {{ des_airport }}
        {%- endif -%}
    </td>
    <tr>
    <td style="width: 50%;">
        <b>{{ _("Bank AD and IFSC:") }}</b> {{ exp_ad_code }} , {{ exp_ifsc }}
    </td>
    {%- if doc.export_type == "Without Payment of Tax" -%}
    <td style="width: 50%;">
        <b>{{ _("LUT:") }}</b> {{ exp_lut }}
    </td>
    {%- endif -%}
    </tr>
</table>


<table class = "table table-condensed table-bordered">
    <tr>
        <th syle="width: 33%;">
            <b>{{ _("Details of Receiver (Bill To)") }}</b>
        </th>
        <th style="width: 33%;">
            <b>{{ _("Details of Consignee (Ship To)") }}</b>
        </th>
        <th style="width: 34%;">
            <b>{{ _("Other Details") }}</b>
        </th>
    </tr>
  <tr>
    <td style="width: 33%;">
        <b>{{ _("Name") }}:</b> {{ frappe.db.get_value("Address", doc.customer_address, "address_title") }}<br>
        <b>{{ _("Address") }}:</b> {{ doc.address_display }} <br>
        {%- if doc.billing_address_gstin|length == 15 -%}
            <b>{{ _("GSTIN") }}:</b> {{ doc.billing_address_gstin }}<br>
        {%- endif -%}
        {%- if frappe.db.get_value("State", (frappe.db.get_value("Address", doc.customer_address, 
            "state_rigpl")), "name"): -%}

            <b>{{ _("State & Code") }}:</b> {{ frappe.db.get_value("Address", 
                doc.customer_address, "state_rigpl") }} {{ _("-")}} {{ _(frappe.db.get_value("State", 
                (frappe.db.get_value("Address", 
                doc.customer_address, "state_rigpl")), "state_code_numeric"))}}

        {%- endif -%}
    </td>
    <td style="width: 33%;">
        <b>{{ _("Name") }}:</b> {{ frappe.db.get_value("Address", doc.shipping_address_name, "address_title") }}<br>
        <b>{{ _("Address") }}:</b> {{ doc.shipping_address }}<br>
        {%- if doc.shipping_address_gstin|length == 15 -%}
            <b>{{ _("GSTIN") }}:</b> {{ doc.shipping_address_gstin }}<br>
        {%- endif -%}
        {%- if frappe.db.get_value("State", (frappe.db.get_value("Address", doc.shipping_address_name, 
            "state_rigpl")), "name"): -%}

            <b>{{ _("State & Code") }}:</b> {{ frappe.db.get_value("Address", 
                doc.shipping_address_name, "state_rigpl") }} {{ _("-")}} {{ _(frappe.db.get_value("State", 
                (frappe.db.get_value("Address", 
                doc.shipping_address_name, "state_rigpl")), "state_code_numeric"))}}

        {%- endif -%}

    </td>
    <td style="width: 34%;">
        <b>{{ _("Date") }}:</b> {{ doc.get_formatted("posting_date") }}<br>
        {%- if doc.removal_date != doc.posting_date -%}
            <b>{{ _("Removal Date") }}:</b> {{ doc.get_formatted("removal_date") }}<br>
            <b>{{ _("Removal Time") }}:</b> {{ doc.get_formatted("removal_time") }}<br>
        {%- endif -%}
        <b>{{ _("Shipment Terms") }}:</b> {{ doc.ship_terms }}<br>
        <b>{{ _("Payment Terms") }}:</b> {{ pay_terms }}<br>
        <b>{{ _("Mode of Shipment") }}:</b> {{ doc.mode_of_transport }}<br>
        <b>{{ _("Ship Via") }}:</b> {{ doc.transporters }}<br>
        <b>{{ _("Docket #") }}:</b> {{ doc.lr_no }}<br>
        {%- if frappe.db.get_value("Sales Taxes and Charges Template", 
            doc.taxes_and_charges, "state"): -%}

            <b>{{ _("Supply State & Code") }}:</b> {{ (" ") }} {{
                frappe.db.get_value("Sales Taxes and Charges Template", 
                doc.taxes_and_charges, "state") }} {{ ("-") }} {{ (
                frappe.db.get_value("State", (frappe.db.get_value("Sales Taxes and Charges Template", 
                doc.taxes_and_charges, "state")), "state_code_numeric")) }}

        {%- endif -%}
    </td>
  </tr>
  <tr>
    {%- if doc.currency != 'INR' -%}
        <td colspan="2"; style="width: 34%;">
            <b>{{ _("PO Date") }}:</b> {{ doc.get_formatted("po_date") }},
            <b>{{ _("PO #") }}:</b> {{ doc.po_no }}
        </td>
        <td>
             <b>{{ _("Currency Info") }}:</b> {{ _("1 -") }}{{ doc.currency }} 
                {{ _("=") }} {{ _(doc.conversion_rate) }} {{ _(" INR") }}
        </td>
    {%- else -%}
        <td>
            <b>{{ _("PO Date") }}:</b> {{ doc.get_formatted("po_date") }},
            <b>{{ _("PO #") }}:</b> {{ doc.po_no }}
        </td>
    {%- endif -%}
  </tr>
    {% set def_bank = frappe.db.get_value("Sales Taxes and Charges Template", doc.taxes_and_charges, "default_bank_account") %}
    {%- if def_bank -%}
        {% set bene = frappe.db.get_value("Bank Account", def_bank, "name_in_bank_records") %}
        {% set bank = frappe.db.get_value("Bank Account", def_bank, "bank") %}
        {% set acc_no = frappe.db.get_value("Bank Account", def_bank, "bank_account_no") %}
        {% set ifsc = frappe.db.get_value("Bank Account", def_bank, "branch_code") %}
        {% set swift = frappe.db.get_value("Bank Account", def_bank, "swift_number") %}
        <tr>
            <td colspan = "3">
                <b>{{ _("Beneficiary Name") }}:</b> {{ bene }},
                <b>{{ _("Bank Name") }}:</b> {{ bank }} <br>
                <b>{{ _("Account No") }}:</b> {{ acc_no }}, 
                {%- if is_export == 1 -%}
                    <b>{{ _("Swift Code") }}:</b> {{ swift }}
                {%- else -%}
                    <b>{{ _("IFSC Code") }}:</b> {{ ifsc }}
                {%- endif -%}
            </td>
        </tr>
    {%- endif -%}
</table>

<table class="table table-condensed table-hover table-bordered">
        <tr>
            <th>Sr</th>
            <th>Description</th>
            <th>HS Code</th>
            <th class="text-right">Qty</th>
            <th class="text-right">Rate</th>
            <th class="text-right">Amount</th>
        </tr>
        {%- for row in doc.items_synopsis -%}
        <tr>
            <td style="width: 3%;">{{ row.idx }}</td>
            <td style="width: 50%;">{{ row.description }}</td>
            <td style="width: 7%;">{{ row.gst_hsn_code }}</td>
            <td style="width: 10%; text-align: right;">{{ row.qty }} {{ row.uom or row.stock_uom }}</td>
            <td style="width: 15%; text-align: right;">{{
                row.get_formatted("rate", doc) }}</td>
            <td style="width: 15%; text-align: right;">{{
                row.get_formatted("amount", doc) }}</td>
        </tr>
        {%- endfor -%}
    </tbody>
</table>
<table class="table table-condensed table-bordered">
  <tr>
    <td width="50%">
        <p>{% if doc.terms %} <b>{{ _("Auxiliary Information") }}: </b>{{ doc.terms or "" }}
        {%- endif -%}
    </p>
    <p style="font-size:12px">{% if doc.in_words %}
        <b>{{ _("Total Amount (In Words)") }}: </b><br>
            {{ doc.in_words }}
        {%- endif -%}
    </p>
    <table class="table table-condensed">
      <tr>
        <td style="width: 100%"; valign="top">
            <b>{{ _("Certified that the particulars given above are True and Correct ") }}</b>
        </td>
      </tr>
      <tr>
        <td style="width: 100%"; height="100px"; valign="top">
            <b>{{ _("For ") }} {{ (exp_name) }}</b>
        </td>
      </tr>
    </table>

    </td>
<td>
        <table class="table table-condensed">
            <tr>
                <td class="text-left" style="width: 40%">
                    {{ _("Net Total") }}
                </td>
                <td class="text-right">
                    {{ doc.get_formatted("net_total") }}
                </td>
            </tr>
            {%- for row in doc.taxes -%}
            {%- if row.tax_amount != 0 -%}
            <tr height:100%>
                <td class="text-left" style="width: 40%">
                    {{ row.description }}
                </td>
                <td class="text-right">
                    {{ row.get_formatted("tax_amount", doc) }}
                </td>
            </tr>
            {%- endif -%}
            {%- endfor -%}
            {%- if doc.discount_amount -%}
            <tr>
                <td class="text-left" style="width: 40%">
                    {{ _("Discount") }}
                </td>
                <td class="text-right">
                    {{ doc.get_formatted("discount_amount") }}
                </td>
            </tr>
            {%- endif -%}
            <tr>
                <td class="text-left" style="width: 40%">
                    <big><b>{{ _("Grand Total") }}</b></big>
                </td>
                <td class="text-right">
                    <big><b>{{ doc.get_formatted("grand_total") }}</b></big>
                </td>
            </tr>
        </td>

        </table>

    </td>
  </tr>
</table>
</small>
